}// 重写获取未定义键Key的行为-(id) valueForUndefinedKey:(NSString *)key{    id value = [unDefinedPool valueForKey:key];    return (value);}-(NSString*)description{    return [NSString stringWithFormat:@"Name:%@,age:%d,weight:%f,currentAddress:%@,address:%@",name,age,weight,currentAddress,addresses];}@end